import { Component } from 'react';
import { Grid, Typography, Button } from '@mui/material';
import { motion, AnimatePresence } from 'framer-motion';
import './App.css';
import { gsap, InertiaPlugin, Draggable, TextPlugin } from "gsap/all";

class HomePage extends Component {
  constructor(props) {
    super(props);
      this.state = {
        openModal: false,
    };

    this.setOpenModal = this.setOpenModal.bind(this);
  }

  setOpenModal() {
    this.setState((prevState) => ({ openModal: !prevState.openModal }));
  }

  componentDidMount() {
    gsap.registerPlugin(Draggable, InertiaPlugin, TextPlugin);

    Draggable.create(".draggable", {
      bounds: ".fullpage-container",
      inertia: true,
      onClick: (e) => {
        console.log('CLICKED', e.target)
        // e.target.style.backgroundColor= 'blue'
    }});

    Draggable.create(".rotation", {
      type: "rotation",
      inertia: true,
      onClick: (e) => {
        console.log('CLICKED', e.target)
        // e.target.style.backgroundColor= 'blue'
    }});

     gsap.to(".title-home", {
        duration: 3,
        text: "Jade & Seka",
        ease: "power1.out",
    });
  }


  render() {
    return (
      <div className="fullpage-container">
      <Grid
      container
      direction="column" // Stack items vertically
      justifyContent="center"
      alignItems="center"
      style={{ minHeight: '100vh' }} // Center vertically in viewport
      >
      {/* Top two images */}
            <Grid container direction="row" justifyContent="center" spacing={2}>
              <Grid item>
                <motion.img
                    src={require('./Earth_icon_2.png')}
                    alt="world 5"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="draggable image-context"
                    transition={{ duration: 3 }}
                />
              </Grid>
              <Grid item>
                <motion.img
                    src={require('./sd_card.png')}
                    alt="world 5"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="draggable image-context"
                    transition={{ duration: 3}}
                />
              </Grid>
              <Grid item>
                <motion.img
                    src={require('./cup.png')}
                    alt="world 5"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    className="draggable image-context"
                    transition={{ duration: 2 }}
                />
              </Grid>
            </Grid>

            {/* Centered Text */}

            <Grid container direction="row" justifyContent="center" spacing={4}>
              <Grid item>
                  <img className="draggable image-context" src={require('./pinkflamingo.png')} alt="flamingo 4" />
              </Grid>
              <Grid justifyContent="center" item>
              <Typography sx={{
                  fontFamily:"Monsieur La Doulaise",
                  fontSize:"5em",
                  fontWeight:"400"
                }}
                className="cedarville-cursive-regula" variant="h2" align="center">
                  Jade & Seka
              </Typography>
              </Grid>
               <Grid item>
                  <img className="draggable image-context" src={require('./58536.png')} alt="flamingo 4" />
              </Grid>
            </Grid>

            {/* Bottom three images */}
            <Grid container direction="row" marginTop={-8} justifyContent="center" spacing={2}>
               <Grid item>
                <Button sx={{backgroundColor:"#2C3607", color:"white"}}>RSVP</Button>
              </Grid>
            </Grid>


          </Grid>
      </div>
    );
  }
}


export default HomePage;
