import './App.css';
import { useEffect, useState } from 'react';
function App() {
    const [data, setData] = useState(null);
    const [nameInput, setNameInput] = useState(null);
    const [error, setError] = useState(false);
    useEffect(() => {
      const weddingPartyData = async () =>{
        try{
          const response = await fetch('https://sheetdb.io/api/v1/a0dbckmyb4l8q');
          const responseJson = await response.json();
          setData(responseJson);
      } catch (error){
        console.log("error fetching data", error);
      }
    }
    weddingPartyData();

  }, []);

  const handleSumbit = () => {
    if(data){
      console.log(data);
      data.array.filter(person => person.fullname === nameInput);
    } else {
      setError(true);
    }
  }

  return (
    <div className="container">
      <div className="form-container">
         <h2 className="cedarville-cursive-regular">Jade and Seka's Wedding Website</h2>
         {error && <span>Name not recognized, contact host</span>}
        <input onChange={(e)=>setNameInput(e.target.value)} placeholder='Please enter your Full Name to access the site'></input>
        <button onClick={handleSumbit}>Submit</button>
      </div>
    </div>
  );
}

export default App;
