import { Component } from 'react';
import { Grid, Typography, Button, Stack, Badge } from '@mui/material';
import { styled } from '@mui/material/styles';
import { motion } from 'framer-motion';
import ModalComponent from './components/ModalComponent';
import './App.css';
import { gsap, InertiaPlugin, Draggable, TextPlugin } from 'gsap/all';
import { homePageImageMeta } from './assets/images/homePageImageMeta';

const navData = [
  'Schedule',
  'Travel Requirements',
  'Registry',
  'Gallery',
  'Things To Do',
  'FAQs',
];

const StyledBadge = styled(Badge)`
     & .MuiBadge-badge {
       background-color: #2C3607;
       color: white;
       font-family: 'Sekasfont-Regular';
       font-size: 1em;
     }
   `;
class HomePage extends Component {
  getSmallScreenHeader() {
    // You can adjust the breakpoint as needed
    if (typeof window !== 'undefined' && window.innerWidth < 600) {
      return (
        <Typography
          variant="h5"
          align="left"
          sx={{ fontFamily: 'Sekasfont-Regular', fontWeight: 400, marginBottom: 2 }}
        >
          Jade & Seka
        </Typography>
      );
    }
    return null;
  }

  constructor(props) {
    super(props);
    // Check if the user is a wedding guest
    if (localStorage.getItem('isWeddingGuest') !== 'true') {
      window.location.href = '/';
      return;
    }
    this.state = {
      isModalOpen: false,
    };
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
  }

  openModal() {
    this.setState({ isModalOpen: true });
  }

  closeModal() {
    this.setState({ isModalOpen: false });
  }

  componentDidMount() {
    gsap.registerPlugin(Draggable, InertiaPlugin, TextPlugin);

    Draggable.create('.draggable', {
      bounds: '.fullpage-container',
      inertia: true,
      onClick: () => {
        this.openModal();
      },
    });
  }


  render() {
    const smallScreenHeader = this.getSmallScreenHeader();
    // Helper to render images by section and row
    const renderImages = (section, row) =>
      homePageImageMeta
        .filter((img) => img.position.section === section && img.position.grid.row === row)
        .map((img) => {
          const commonProps = {
            key: img.name,
            alt: img.alt,
            className: 'draggable image-context',
            style: { width: img.width, height: img.height },
          };
          if (img.motion) {
            return (
              <Grid item key={img.name}>
                <StyledBadge
                  badgeContent={img.key}
                  anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
                >
                  <motion.img
                    src={img.src}
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: img.transitionDuration || 2 }}
                    {...commonProps}
                  />
                </StyledBadge>
              </Grid>
            );
          }
          return (
            <Grid item key={img.name}>
              <StyledBadge
                badgeContent={img.key}
                color="primary"
                anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
              >
                <img src={img.src} alt={img.alt || ''} {...commonProps} />
              </StyledBadge>
            </Grid>
          );
        });

    return (
      <div>
        <Grid
          container
          direction="column"
          justifyContent="center"
          alignItems="center"
          style={{ minHeight: '100vh' }}
        >
          {smallScreenHeader}
          {/* Top images row */}
          <Grid container direction="row" justifyContent="center" spacing={2}>
            {renderImages('top', 1)}
          </Grid>

          {/* Title and Navigation */}
          <Grid justifyContent="center" item>
            <Stack alignItems="center" justifyContent="center">
              <Typography
                sx={{ fontFamily: 'Sekasfont-Regular', fontSize: '5em', fontWeight: 400 }}
                variant="h2"
                align="center"
              >
                Jade & Seka
              </Typography>
              <Button
                justifyContent="center"
                onClick={this.openModal}
                sx={{ fontFamily: 'Sekasfont-Regular', fontSize: '18px', backgroundColor: '#2C3607', color: 'white' }}
              >
                RSVP
              </Button>
              <ol className="nav-inline-list">
                {navData.map((listItem, idx) => [
                  <li key={listItem}>{listItem}</li>,
                  idx === 2 ? <br key="break" /> : null,
                ])}
              </ol>
            </Stack>
          </Grid>

          {/* Bottom Images Row */}
          <Grid container direction="row" justifyContent="center" spacing={4}>
            {renderImages('bottom', 2)}
          </Grid>
        </Grid>
        {this.state.isModalOpen && (
          <ModalComponent open={this.state.isModalOpen} onClose={this.closeModal}>
            <Typography variant="h6" id="modal-title">
              Modal Title
            </Typography>
            <Typography id="modal-description">
              This is the modal content.
            </Typography>
          </ModalComponent>
        )}
      </div>
    );
  }
}


export default HomePage;
