import './App.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import gsap from 'gsap';
import { useGSAP } from '@gsap/react';

function LandingPage() {
  gsap.registerPlugin(useGSAP);

  const [nameInput, setNameInput] = useState(null);
  const [error, setError] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(false);

    if (!nameInput) {
      setError(true);
      return;
    }

    try {
      const response = await fetch(
        `https://sheetdb.io/api/v1/a0dbckmyb4l8q/search?fullname=${encodeURIComponent(nameInput)}`
      );
      const results = await response.json();
      if (results.length) {
        setError(false);
        localStorage.setItem('isWeddingGuest', 'true');
        navigate('/home');
      }
      else {
        setError(true);
      }
    } catch (err) {
      setError(true);
    }
  };

  return (
    <div className="container">
      <form className="form-container" onSubmit={handleSubmit}>
          <h2 id="landing-page-text" className="sekasfont-regular" style={{fontSize:'3em'}}>Jade & Seka's Wedding Website</h2>
          {error && <span className="password-error">Name not recognized, please contact host</span>}
          <input onChange={(e)=>setNameInput(e.target.value)} placeholder='Please enter your Full Name to access the site'></input>
          <button className="landing-page-button" type="submit">ENTER</button>
        </form>
    </div>
  );
}

export default LandingPage;
