import './App.css';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import gsap from 'gsap';
import { useGSAP } from 'gsap';

function LandingPage() {
  gsap.registerPlugin(useGSAP);

  const [nameInput, setNameInput] = useState(null);
  const [error, setError] = useState(false);
  const navigate = useNavigate();

  useGSAP(()=>{
    gsap.to("#landing-page-text", {
      duration: 2,
      text: "Jade & Seka's Wedding Website",
      ease: "none",
    });
  });

  const handleSumbit = async (event) => {
    event.preventDefault();

    if (!nameInput) setError(true);

    const response = await fetch('https://sheetdb.io/api/v1/a0dbckmyb4l8q');
    const data = await response.json();
    const results = data.filter(person=> person.fullname === nameInput);
    if(!results.length) {
      setError(true);
    } else{
      setError(false);
      navigate('/home');
    }    
  }

  return (
    <div className="container">
      <form className="form-container" onSubmit={handleSumbit}>
          <h2 id="landing-page-text" className="homemade-apple-regular">Welcome to</h2>
          {error && <span className="password-error">Name not recognized, please contact host</span>}
          <input onChange={(e)=>setNameInput(e.target.value)} placeholder='Please enter your Full Name to access the site'></input>
          <button type="submit">Submit</button>
        </form>
    </div>
  );
}

export default LandingPage;
