import './App.css';
import { useState } from 'react';
function App() {
  const [nameInput, setNameInput] = useState(null);
  const [error, setError] = useState(false);


  const handleSumbit = async (event) => {
    event.preventDefault();

    if (!nameInput) setError(true);

    const response = await fetch('https://sheetdb.io/api/v1/a0dbckmyb4l8q');
    const data = await response.json();
    const results = data.filter(person=> person.fullname === nameInput);
    if(!results.length) {
      setError(true);
    } else{
      setError(false)
    }    
  }

  return (
    <div className="container">
      <form className="form-container" onSubmit={handleSumbit}>
          <h2 className="cedarville-cursive-regular">Jade and Seka's Wedding Website</h2>
          {error && <span className="password-error">Name not recognized, please contact host</span>}
          <input onChange={(e)=>setNameInput(e.target.value)} placeholder='Please enter your Full Name to access the site'></input>
          <button type="submit">Submit</button>
        </form>
    </div>
  );
}

export default App;
